mapper.insert:
insert into boardlike(LIKENO , wbnum , userid)
values((SELECT NVL(MAX(likeno), 0) + 1 FROM boardlike) ,#{wbNum}
,#{userId})

mapper.update:
update boardlike set LIKECHECK = 1
where wbNum =
#{wbNum} and userId = #{userId}

update boardlike set LIKECHECK = 0
where wbNum =
#{wbNum} and userId = #{userId}
